catalog_script_client.script:
function onLoad() {
    // 仅在有窗口环境时运行 (防止后台报错)
    if (typeof window === 'undefined' || !window.document) {
        return;
    }

    // 获取根元素 (用于设置 CSS 变量)
    var root = window.document.documentElement;

    // 定义眼球移动的最大范围 (像素)
    // 值越大，眼睛跟着鼠标跑得越远
    var moveRange = 25; 

    // 监听整个文档的鼠标移动事件
    window.document.addEventListener('mousemove', function(e) {
        try {
            // 获取窗口中心点坐标
            var centerX = window.innerWidth / 2;
            var centerY = window.innerHeight / 2;

            // 获取鼠标当前相对于窗口中心的位置偏移量
            // e.clientX/Y 是鼠标坐标
            var offsetX = e.clientX - centerX;
            var offsetY = e.clientY - centerY;

            // 将偏移量标准化到 -1 到 1 之间
            // 除以屏幕宽高的一半
            var normalizedX = offsetX / (window.innerWidth / 2);
            var normalizedY = offsetY / (window.innerHeight / 2);

            // 计算眼球应该移动的距离 (标准化值 * 最大范围)
            // 使用 Math.min/max 限制边界，防止眼球飞出眼眶
            var moveX = Math.max(Math.min(normalizedX * moveRange, moveRange), -moveRange);
            var moveY = Math.max(Math.min(normalizedY * moveRange, moveRange * 0.6), -moveRange * 0.6); // Y轴移动稍微小一点更自然

            // 实时更新 CSS 变量
            root.style.setProperty('--eye-x', moveX + 'px');
            root.style.setProperty('--eye-y', moveY + 'px');

        } catch (err) {
            // 避免报错影响其他脚本
            console.log('Eye tracker error: ' + err);
        }
    });
}
