                        var diffMs = GlideDateTime.subtract(now, created).getNumericValue();
                        var minutes = Math.floor(diffMs / 1000 / 60);

                        var timeAgo = '';
                        if (minutes < 60) {
                            timeAgo = minutes + "åˆ†å‰";
                        } else if (minutes < 1440) {
                            timeAgo = Math.floor(minutes / 60) + "æ™‚é–“å‰";
