sp_widget.client_script:
api.controller = ['$scope', '$element', '$timeout', '$window', '$interval', '$http', function($scope, $element, $timeout, $window, $interval, $http) {
  /* ═══════════════════════════════════════
     SERVICE NOW WIDGET CLIENT SCRIPT
     终极版：高保真粒子 + 智能重定向逻辑
     ═══════════════════════════════════════ */

  var c = this;
  console.log('Widget Loaded: KOKORO System Ready');

  // ── 1. 初始化变量 ──
  c.username = '';
  c.password = '';
  c.loading = false;
  c.btnText = 'LOG IN';
  c.currentTime = '--:--:--';
  c.language = 'English'; 
  c.sessionId = 'SID-' + Math.floor(Math.random() * 90000 + 10000);

  // ── 2. 实时时钟 ──
  var clockTimer = $interval(function() {
    var now = new Date();
    var pad = function(n) { return n < 10 ? '0' + n : n; };
    c.currentTime = pad(now.getHours()) + ':' + pad(now.getMinutes()) + ':' + pad(now.getSeconds());
  }, 1000);

  // ── 3. 登录逻辑 (智能跳转版) ──
  c.login = function() {
    console.log("Login sequence initiated...");

    if (!c.username || !c.password) {
        return;
    }

    c.loading = true;
    c.btnText = 'AUTHENTICATING...';

    // ── A. 智能获取重定向目标 ──
    function getParameterByName(name) {
        var url = $window.location.href;
        name = name.replace(/[\[\]]/g, '\\$&');
        var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)');
        var results = regex.exec(url);
        if (!results) return null;
        if (!results[2]) return '';
        return decodeURIComponent(results[2].replace(/\+/g, ' '));
    }

    // 检查是否有系统传来的重定向目标
    var redirectUrl = getParameterByName('sysparm_redirect');

    // 如果没有重定向目标，则默认去 volunteer
    var finalRedirect = redirectUrl ? redirectUrl : '?id=kokoro_volunteer'; 

    console.log("Target Redirect URL:", finalRedirect);

    // ── B. 构建登录请求 ──
    var params = {
      'user_name': c.username,
      'user_password': c.password,
      'sys_action': 'sysverb_login',
      'sysparm_login_url': finalRedirect 
    };

    // ── C. 发送请求 ──
    $http({
      method: 'POST',
      url: '/login.do',
      headers: {'Content-Type': 'application/x-www-form-urlencoded'},
      transformRequest: function(obj) {
        var str = [];
        for(var p in obj) str.push(encodeURIComponent(p) + "=" + encodeURIComponent(obj[p]));
        return str.join("&");
      },
      data: params
    }).then(function(response) {
      // 登录成功
      c.btnText = 'ACCESS GRANTED';

      $timeout(function() {
          // 执行跳转
          if (finalRedirect.startsWith('http') || finalRedirect.startsWith('/')) {
               $window.location.href = finalRedirect;
          } else {
               $window.location.href = finalRedirect;
          }
      }, 1000);

    }, function(error) {
      // 登录失败
      console.error('Auth Failed:', error);
      c.loading = false;
      c.btnText = 'ACCESS DENIED';

      $timeout(function() { 
          c.btnText = 'LOG IN'; 
          c.password = ''; 
      }, 2000);
    });
  };

  // ── 4. 粒子系统 (修复版：解决粒子堆积在左侧的问题) ──
  function initParticles() {
    try {
      var canvas = $element[0].querySelector('#particle-canvas');
      if (!canvas) return;

      var ctx = canvas.getContext('2d');
      var w, h, particles = [];
      var animationId;
      var i, j; 

      // [步骤1] 提前定义粒子类 (必须在 resize 之前定义)
      var Particle = function() {
        this.reset = function() {
          this.x = Math.random() * w;
          this.y = Math.random() * h;
          this.vx = (Math.random() - 0.5) * 0.5; 
          this.vy = (Math.random() - 0.5) * 0.5;
          this.size = Math.random() * 3 + 1.5; 
          this.color = 'rgba(231, 76, 60, ' + (Math.random() * 0.5 + 0.4) + ')';
        };
        this.reset();
      };

      // [步骤2] 封装粒子生成逻辑
      var spawnParticles = function() {
          particles = []; // 清空旧粒子
          // 根据新屏幕面积重新计算粒子数量
          var count = Math.min(80, Math.floor((w * h) / 15000));
          for (i = 0; i < count; i++) { 
            particles.push(new Particle()); 
          }
      };

      // [步骤3] 调整大小函数 (包含重置逻辑)
      var resize = function() {
        w = canvas.width = $window.innerWidth;
        h = canvas.height = $window.innerHeight;
        // [核心修复] 窗口变动时，立刻重新散布粒子
        spawnParticles();
      };

      angular.element($window).on('resize', resize);

      // 初始化执行一次
      resize();

      var animate = function() {
        if (!ctx) return;
        ctx.clearRect(0, 0, w, h);

        // 绘制粒子
        for (i = 0; i < particles.length; i++) {
          var p = particles[i];
          p.x += p.vx; p.y += p.vy;
          if (p.x < 0 || p.x > w || p.y < 0 || p.y > h) p.reset();

          ctx.beginPath();
          ctx.arc(p.x, p.y, p.size, 0, Math.PI * 2);
          ctx.fillStyle = p.color;
          ctx.fill();
        }

        // 绘制连线
        for (i = 0; i < particles.length; i++) {
          for (j = i + 1; j < particles.length; j++) {
            var dx = particles[i].x - particles[j].x;
            var dy = particles[i].y - particles[j].y;
            var dist = Math.sqrt(dx*dx + dy*dy);

            // [视觉调整] 连线距离加大到 150，线条加粗
            if (dist < 150) {
              ctx.beginPath();
              // [视觉调整] 线条透明度系数提升到 0.4
              ctx.strokeStyle = 'rgba(231, 76, 60, ' + (1 - dist/150) * 0.4 + ')';
              ctx.lineWidth = 1.2; 
              ctx.moveTo(particles[i].x, particles[i].y);
              ctx.lineTo(particles[j].x, particles[j].y);
              ctx.stroke();
            }
          }
        }
        animationId = $window.requestAnimationFrame(animate);
      };
      animate();

      $scope.$on('$destroy', function() {
        if (animationId) $window.cancelAnimationFrame(animationId);
        angular.element($window).off('resize', resize);
      });

    } catch (e) { console.error(e); }
  }

  // 延迟启动粒子以确保 DOM 加载
  $timeout(initParticles, 100);

  // ── 5. 键盘事件监听 ──
  var handleEnter = function(e) {
    if (e.key === 'Enter') {
      $scope.$apply(function() { c.login(); });
    }
  };
  document.addEventListener('keydown', handleEnter);

  $scope.$on('$destroy', function() {
    document.removeEventListener('keydown', handleEnter);
    if (clockTimer) $interval.cancel(clockTimer);
  });
}];

