                    data.deleted_id = delSysId;
                    gs.info('[Kokoro] Broadcast deactivated by Admin: ' + delSysId);
                    VolRateLimiter.record(delBcVolId, 'DELETE_BC');
                }
            }
        } catch (e) {
            data.error = 'Delete failed: ' + e.message;
        }
        return;
    }

