sys_script.script:
(function executeRule(current, previous) {

    gs.info('[Kokoro BR] Task assigned: ' + current.number + ' to ' + current.assigned_to.getDisplayValue());

    // 推进SLA从response到resolution阶段
    try {
        var slaEngine = new x_1821654_kokoro_0.KokoroSLAEngine();
        slaEngine.advanceSLAStage(current.getUniqueValue());
    } catch (e) {
        gs.error('[Kokoro BR] SLA stage advance error: ' + e.message);
    }

    // 如果是手动分配（非自动分配），记录历史
    // 自动分配已经在KokoroAutoAssignment中记录了
    // 这里检查work_notes是否包含自动分配标记
    var isAutoAssign = current.work_notes && current.work_notes.toString().indexOf('[自動分配]') >= 0;

    if (!isAutoAssign && current.assigned_to.changes()) {
        try {
            var histGR = new GlideRecord('x_1821654_kokoro_0_assignment_history');
            histGR.initialize();
            histGR.setValue('wish', current.getUniqueValue());
            histGR.setValue('assigned_to', current.getValue('assigned_to'));
            histGR.setValue('assigned_by', gs.getUserID());
            histGR.setValue('assignment_type', 'manual');
            histGR.setValue('assignment_reason', '手動割り当て');
            histGR.setValue('timestamp', new GlideDateTime());

            if (previous.assigned_to && !previous.assigned_to.nil()) {
                histGR.setValue('previous_assignee', previous.getValue('assigned_to'));
            }

            histGR.insert();
        } catch (e) {
            gs.error('[Kokoro BR] Assignment history error: ' + e.message);
        }
    }

})(current, previous);
