sys_script.script:
(function executeRule(current, previous) {

    // 1. 构建地址
    var address = buildAddress(current);
    if (!address) return;

    // 2. 调用 Mapbox (在后台执行)
    var geocoder = new x_1821654_kokoro_0.MapboxGeocoder();
    var result = geocoder.geocode(address);

    // 3. 处理结果
    if (result.success) {
        // ✅ 成功 - 设置坐标
        // 请确保这里的字段名和你表里的真实字段名一致 (latitude vs u_latitude)
        current.setValue('latitude', result.lat); 
        current.setValue('longitude', result.lng);
        current.setValue('geocode_status', 'success');
        current.setValue('precise_location', result.place_name || address);

        // ⚠️ Async 规则必须手动保存！
        current.update(); 

        gs.info('[Geo] Background update success: ' + current.number);
    } else {
        // ❌ 失败
        current.setValue('geocode_status', 'failed');

        // ⚠️ Async 规则必须手动保存！
        current.update(); 

        gs.warn('[Geo] Background update failed: ' + result.error);
    }

    function buildAddress(record) {
        var parts = [];
        var zone = record.getValue('shelter_zone');
        var detail = record.getValue('detail_loc');

        if (detail) parts.push(detail);
        if (zone) parts.push(zone);
        if (parts.length === 0) return null;

        parts.push('Tokyo, Japan'); // 默认后缀
        return parts.join(', ');
    }

})(current, previous);
