sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // 1. 获取用户填写的紧急度
    var urgency = current.u_urgency; // 确保字段名是对的，通常是 u_urgency

    // 2. 根据紧急度设置 Triage Level 和 Mood Color
    // 假设 Triage Level 是 choice: Red, Yellow, Green
    // 假设 Mood Color 是 string 或 choice: Red, Yellow, Green

    if (urgency == 'Critical') {
        current.setValue('triage_level', 'Red'); 
        current.setValue('mood_color', 'Red');
        current.setValue('ai_sentiment', '【システム判定】直ちに対応が必要です (Critical)');
    } 
    else if (urgency == 'High') {
        current.setValue('triage_level', 'Orange'); // 或 Red/Yellow
        current.setValue('mood_color', 'Orange');
        current.setValue('ai_sentiment', '【システム判定】優先的に対応してください (High)');
    }
    else if (urgency == 'Medium') {
        current.setValue('triage_level', 'Yellow');
        current.setValue('mood_color', 'Yellow');
        current.setValue('ai_sentiment', '【システム判定】順次対応します (Normal)');
    }
    else { // Low
        current.setValue('triage_level', 'Green');
        current.setValue('mood_color', 'Green');
        current.setValue('ai_sentiment', '【システム判定】状況は安定しています (Low)');
    }

})(current, previous);
