sys_script.script:
(function executeRule(current, previous) {

       // 只在分配志愿者时执行
       if (!current.assigned_to.nil() && previous.assigned_to.nil()) {

           // 根据紧急度计算基础时间（分钟）
           var baseTime = 30; // 默认30分钟
           var urgency = current.getValue('u_urgency');

           switch(urgency) {
               case 'Critical':
                   baseTime = 10;
                   break;
               case 'High':
                   baseTime = 20;
                   break;
               case 'Medium':
                   baseTime = 30;
                   break;
               case 'Low':
                   baseTime = 45;
                   break;
           }

           // 根据区域调整时间
           var zone = current.getValue('shelter_zone');
           if (zone == 'Medical Area') {
               baseTime += 5; // 医疗区稍远
           }

           // 计算预计到达时间
           var now = new GlideDateTime();
           var eta = new GlideDateTime(now);
           eta.addSeconds(baseTime * 60);

           // 更新字段（需要先创建这个字段）
           current.setValue('u_estimated_arrival', eta);
           current.setWorkflow(false);
           current.update();

           gs.info('ETA calculated for ' + current.number + ': ' + baseTime + ' minutes');
       }

   })(current, previous);
