sys_script.script:
(function executeRule(current, previous /*null when insert*/) {

    // 只在状态变为SUBMITTED (10) 时触发
    if (current.state != 10) return;

    gs.info('[Kokoro BR] Processing new submission: ' + current.number);

    // 1. 附加SLA
    try {
        var slaEngine = new x_1821654_kokoro_0.KokoroSLAEngine();
        var slaResult = slaEngine.attachSLA(current.getUniqueValue());

        if (slaResult.success) {
            gs.info('[Kokoro BR] SLA attached to ' + current.number);
        }
    } catch (e) {
        gs.error('[Kokoro BR] SLA attach error: ' + e.message);
    }

    // 2. 如果未分配，尝试自动分配
    if (current.assigned_to.nil()) {
        try {
            var assignEngine = new x_1821654_kokoro_0.KokoroAutoAssignment();
            var assignResult = assignEngine.autoAssign(current.getUniqueValue());

            if (assignResult.success) {
                gs.info('[Kokoro BR] Auto-assigned ' + current.number + ' to ' + assignResult.volunteer_name);
                // 刷新current以获取更新后的值
                current.assigned_to.setDisplayValue(assignResult.volunteer_name);
            } else {
                gs.info('[Kokoro BR] No auto-assignment for ' + current.number + ': ' + assignResult.reason);
            }
        } catch (e) {
            gs.error('[Kokoro BR] Auto-assign error: ' + e.message);
        }
    }

})(current, previous);
