sys_script.script:
(function executeRule(current, previous) {

    // 1. 初始化验证器 (如果 Script Include 没配好，则使用后备逻辑)
    var validator;
    try {
        validator = new StateTransitionValidator();
    } catch (e) {
        validator = null;
    }

    var fromState = previous.state.toString();
    var toState = current.state.toString();

    // 如果状态没变，直接返回
    if (fromState === toState) return;

    // 2. 定义允许的转换 (使用文本值匹配您之前创建的 Choice List)
    // 请确保这些值(draft, submitted等)与您在 Choice List 的 'Value' 列完全一致
    var allowedTransitions = {
        'draft':       ['submitted', 'Cancelled'],
        'submitted':   ['draft', 'assigned', 'Cancelled'],
        'assigned':    ['submitted', 'In Progress', 'Cancelled', 'On Hold'],
        'In Progress': ['Completed', 'Cancelled', 'On Hold'],
        'Completed':   [], 
        'Cancelled':   [], 
        'On Hold':     ['assigned', 'In Progress', 'Cancelled']
    };

    // 3. 检查转换是否允许
    // 优先使用 Validator 类，如果没有则使用上面的本地配置
    var isAllowed = false;
    if (validator) {
        var check = validator.validateTransition(fromState, toState, current);
        isAllowed = check.valid;
    } else {
        var allowed = allowedTransitions[fromState] || [];
        isAllowed = (allowed.indexOf(toState) !== -1);
    }

    if (!isAllowed) {
        current.state = previous.state; // 回滚状态
        gs.addErrorMessage('Status transition not allowed: ' + fromState + ' -> ' + toState);
        current.setAbortAction(true);
        return;
    }

    // 4. 记录到 Process Event 表 (日志)
    try {
        var pe = new GlideRecord('x_1821654_kokoro_0_process_event');
        pe.initialize();
        // 确保字段名带有 u_ 前缀 (这是自定义表的标准)
        pe.setValue('u_case_id', current.getUniqueValue());
        pe.setValue('u_activity', 'State Change: ' + fromState + ' -> ' + toState);
        pe.setValue('u_user', gs.getUserID());
        // pe.setValue('u_timestamp', new GlideDateTime()); // 系统会自动记录 sys_created_on
        pe.insert();
    } catch (e) {
        gs.warn('[Kokoro] Process event logging failed: ' + e.message);
    }

    // 5. 更新当前记录的审计字段
    // 确保您在 Silent Wish 表中已经创建了这两个字段
    if (current.isValidField('u_state_changed_on')) {
        current.setValue('u_state_changed_on', new GlideDateTime());
    }
    if (current.isValidField('u_state_changed_by')) {
        current.setValue('u_state_changed_by', gs.getUserID());
    }

})(current, previous);
