sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // 1. 获取许愿内容
    // 根据你的列表截图，你的字段标签是 "Wish Content"
    // 在 Scoped App 中，通常字段名是 'wish_content' (如果不对，请在 Dictionary 里确认一下这个字段的 Column name)
    var text = (current.wish_content + "").toLowerCase(); 

    // 2. 定义关键词 (与之前的逻辑保持一致)
    var urgentWords = ['痛', '血', '急', '救', '死', '伤', 'help', 'pain'];
    var warmWords = ['冷', '寒', '饿', '渴', '水', '药', '被', 'cold', 'water'];

    // 3. 检测逻辑
    var isUrgent = urgentWords.some(function(word) { return text.indexOf(word) > -1; });
    var isWarm = warmWords.some(function(word) { return text.indexOf(word) > -1; });

    // 4. 设置 Mood Color 字段的值
    // 【关键修改】根据你的截图，字段名是 'mood_color'，不是 'u_mood_color'
    if (isUrgent) {
        current.mood_color = 'Red';  
    } 
    else if (isWarm) {
        current.mood_color = 'Orange'; 
    } 
    else {
        current.mood_color = 'Blue';   
    }

})(current, previous);
