sys_script.script:
(function executeRule(current, previous) {

    // 1. 初始化验证器
    var validator = new StateTransitionValidator();
    var fromState = previous.state.toString(); // 确保 state 字段名正确
    var toState = current.state.toString();

    // 2. 验证状态转换
    var result = validator.validateTransition(fromState, toState, current);

    if (!result.valid) {
        // 验证失败：阻止保存并报错
        current.setAbortAction(true);
        current.state = previous.state;
        gs.addErrorMessage(result.message);
        return;
    }

    // 3. 记录日志到 Process Event 表 (确保表名和字段名正确)
    var pe = new GlideRecord('x_1821654_kokoro_0_process_event');
    pe.initialize();

    // 注意：这里假设您在 Process Event 表中建立的字段是以 u_ 开头的
    // 如果您建立的字段名没有 u_，请手动删除代码里的 u_
    pe.setValue('u_case_id', current.getUniqueValue()); 
    pe.setValue('u_activity', 'State Change: ' + fromState + ' -> ' + toState);
    pe.setValue('u_user', gs.getUserID());

    // 不需要单独写 timestamp，系统会自动记录 sys_created_on
    // pe.setValue('u_timestamp', new GlideDateTime()); 

    pe.insert();

    // 4. 更新当前记录的变更信息 (确保您在 Heartbeat 表里加了这俩字段)
    // 同样假设您新建的字段自动带了 u_ 前缀
    current.setValue('u_state_changed_on', new GlideDateTime());
    current.setValue('u_state_changed_by', gs.getUserID());

})(current, previous);
