sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    // 获取事务记录的信息
    var inventoryId = current.getValue('inventory_item');
    var quantity = parseInt(current.getValue('quantity'));

    // 验证必要字段
    if (!inventoryId || !quantity) {
        gs.error('Auto Update Inventory: Missing required fields');
        return;
    }

    // 获取库存记录
    var inventory = new GlideRecord('x_1821654_kokoro_0_kokoro_inventory');
    inventory.setWorkflow(false); // 防止触发其他 Business Rules 或 Flows

    if (inventory.get(inventoryId)) {
        var currentQty = parseInt(inventory.getValue('quantity'));
        var newQty = currentQty - quantity;

        // 检查库存是否足够
        if (currentQty >= quantity) {
            // 更新库存
            inventory.setValue('quantity', newQty);
            inventory.update();

            gs.info('Inventory updated: ' + inventory.getValue('item_name') + 
                   ' | Old: ' + currentQty + ' | Consumed: ' + quantity + 
                   ' | New: ' + newQty);

            // 如果库存低于阈值，发送警报
            var threshold = parseInt(inventory.getValue('reorder_point')) || 10;
            if (newQty <= threshold) {
                // 创建事件或发送通知
                gs.eventQueue('kokoro.inventory.low', inventory, newQty, threshold);
                gs.warn('Low inventory alert: ' + inventory.getValue('item_name') + 
                       ' (Current: ' + newQty + ', Threshold: ' + threshold + ')');
            }

        } else {
            // 库存不足，记录错误
            gs.error('Insufficient inventory: ' + inventory.getValue('item_name') + 
                    ' | Available: ' + currentQty + ' | Requested: ' + quantity);

            // 可以在这里添加通知逻辑
            gs.eventQueue('kokoro.inventory.insufficient', inventory, currentQty, quantity);
        }
    } else {
        gs.error('Inventory item not found: ' + inventoryId);
    }

})(current, previous);
