sys_security_acl.script:
(function executeRule(current, previous) {
         // 管理者およびボランティアはすべてのレコードを閲覧可能
         if (gs.hasRole('admin') || gs.hasRole('x_1821654_kokoro.volunteer')) {
             return true;
         }

         // 被災者は自身が作成したレコードのみ閲覧可能
         if (gs.hasRole('x_1821654_kokoro.refugee')) {
             return current.getValue('opened_by') == gs.getUserID();
         }

         // 上記以外はアクセス権限なし
         return false;
     })(current, previous);
