sysauto_script.script:
(function() {
    gs.info('[Kokoro Scheduled] Generating daily SLA report...');

    try {
        var yesterday = new GlideDateTime();
        yesterday.addDaysLocalTime(-1);
        var yesterdayStr = yesterday.getLocalDate().toString();

        // 统计昨天的SLA数据
        var stats = {
            total: 0,
            achieved: 0,
            breached: 0,
            avgResolutionTime: 0
        };

        var slaGR = new GlideRecord('x_1821654_kokoro_0_sla_task');
        slaGR.addQuery('actual_end_time', '>=', yesterdayStr + ' 00:00:00');
        slaGR.addQuery('actual_end_time', '<=', yesterdayStr + ' 23:59:59');
        slaGR.query();

        var totalResolutionMinutes = 0;

        while (slaGR.next()) {
            stats.total++;

            if (slaGR.getValue('status') === 'achieved') {
                stats.achieved++;
            } else if (slaGR.getValue('status') === 'breached') {
                stats.breached++;
            }

            // 计算解决时间
            var startTime = new GlideDateTime(slaGR.getValue('start_time'));
            var endTime = new GlideDateTime(slaGR.getValue('actual_end_time'));
            var resolutionMs = GlideDateTime.subtract(startTime, endTime).getNumericValue();
            totalResolutionMinutes += Math.floor(resolutionMs / 1000 / 60);
        }

        if (stats.total > 0) {
            stats.avgResolutionTime = Math.round(totalResolutionMinutes / stats.total);
            stats.achievementRate = Math.round((stats.achieved / stats.total) * 100);
        }

        var report = '[Kokoro Daily Report] ' + yesterdayStr + '\n' +
                    '- Total SLAs completed: ' + stats.total + '\n' +
                    '- Achieved: ' + stats.achieved + ' (' + (stats.achievementRate || 0) + '%)\n' +
                    '- Breached: ' + stats.breached + '\n' +
                    '- Average resolution time: ' + stats.avgResolutionTime + ' minutes';

        gs.info(report);

        // TODO: 可以将报告发送邮件
        // gs.eventQueue('kokoro.daily_report', null, report);

    } catch (e) {
        gs.error('[Kokoro Scheduled] Daily report error: ' + e.message);
    }

})();
