sysauto_script.script:
(function() {
    gs.info('[Kokoro Scheduled] Starting volunteer task count sync...');

    try {
        var updated = 0;
        var capGR = new GlideRecord('x_1821654_kokoro_0_volunteer_capability');
        capGR.query();

        while (capGR.next()) {
            var volunteerId = capGR.getValue('volunteer');

            // 计算实际活跃任务数
            var ga = new GlideAggregate('x_1821654_kokoro_0_silent_wish');
            ga.addQuery('assigned_to', volunteerId);
            ga.addQuery('state', 'IN', '11,2'); // ASSIGNED or IN_PROGRESS
            ga.addAggregate('COUNT');
            ga.query();

            var actualCount = 0;
            if (ga.next()) {
                actualCount = parseInt(ga.getAggregate('COUNT')) || 0;
            }

            var storedCount = parseInt(capGR.getValue('current_task_count')) || 0;

            // 如果不一致，更新
            if (actualCount !== storedCount) {
                capGR.setValue('current_task_count', actualCount);
                capGR.update();
                updated++;
                gs.info('[Kokoro Scheduled] Corrected task count for volunteer ' + 
                       capGR.volunteer.getDisplayValue() + ': ' + storedCount + ' -> ' + actualCount);
            }
        }

        gs.info('[Kokoro Scheduled] Task count sync complete: ' + updated + ' records updated');

    } catch (e) {
        gs.error('[Kokoro Scheduled] Task count sync error: ' + e.message);
    }

})();
