sysauto_script.script:
(function() {
    gs.info('[Kokoro Scheduled] Starting SLA status update...');

    try {
        var slaEngine = new x_1821654_kokoro_0.KokoroSLAEngine();
        var stats = slaEngine.updateAllSLAStatus();

        gs.info('[Kokoro Scheduled] SLA update complete: ' + 
               'processed=' + stats.processed + ', ' +
               'ok=' + stats.ok + ', ' +
               'warnings=' + stats.warnings + ', ' +
               'critical=' + stats.critical + ', ' +
               'breaches=' + stats.breaches);

        // 如果有违规，可以触发额外的通知
        if (stats.breaches > 0) {
            gs.warn('[Kokoro Scheduled] ' + stats.breaches + ' SLA breaches detected!');
        }

    } catch (e) {
        gs.error('[Kokoro Scheduled] SLA update error: ' + e.message);
    }

})();
