sysauto_script.script:
// ==========================================
// Kokoro Watchdog - 自动化生命监测脚本 (修正版)
// ==========================================

// 1. 使用正确的长表名
var gr = new GlideRecord('x_1821654_kokoro_0_x_1821654_kokoro_0_heartbeat');
gr.addQuery('u_status', 'Safe');

// 2. 核心算法：找出 "最后更新时间" 在 "24小时之前" 的记录
// encodedQuery 是最高效的查询方式
gr.addEncodedQuery('sys_updated_onRELATIVELT@hour@ago@24'); 

gr.query();

gs.info("[Kokoro Watchdog] 启动扫描... 发现 " + gr.getRowCount() + " 个超时目标。");

while (gr.next()) {
    // 3. 找到超时用户了！开始处理
    var userName = gr.u_user.getDisplayValue() || "Guest";
    gs.info("[Kokoro Watchdog] 警告: 用户 " + userName + " 心跳超时。正在升级状态...");

    // 4. 修改状态为 Warning (预警)
    gr.setValue('u_status', 'Warning');
    gr.update();

    // 5. 发射神经信号 (Trigger Event)
    // 请确保 Event Registry 里的 Table 也是选的这张长名字的表，否则事件也会断开
    gs.eventQueue('x_1821654_kokoro.heartbeat_timeout', gr, gr.u_user, gr.u_last_message);
}
